mapper.select:
		select 

		from user


  				id = #{id}


  				and username = #{username}


  				and password = #{password}


  				and authority = #{authority}


  				and first_name = #{firstName}


  				and last_name = #{lastName}


  				and city = #{city}


  				and country = #{country}


  				and address = #{address}


  				and phone_num = #{phoneNum}


  				and photo_src = #{photoSrc}
